// appium android check region with ignore region
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({
      device: 'Samsung Galaxy S8',
      app:
        'https://applitools.bintray.com/Examples/eyes-android-hello-world.apk',
    })
    eyes = testSetup.getEyes({
      displayName: 'appium android check region with ignore region',
      baselineName: 'Appium_Android_CheckRegionWithIgnoreRegion',
      branchName: 'javascript_mobile',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('appium android check region with ignore region (@native-selectors @native @mobile @headfull)', async () => {
    await spec.click(driver, {
      type: 'class name',
      selector: 'android.widget.Button',
    })
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'Appium_Android_CheckRegionWithIgnoreRegion',
      undefined
    )
    await eyes.check({
      region: {
        type: 'id',
        selector: 'com.applitools.helloworld.android:id/image_container',
      },
      ignoreRegions: [
        {
          type: '-android uiautomator',
          selector:
            'new UiSelector().textContains("You successfully clicked the button!")',
        },
        {type: 'id', selector: 'com.applitools.helloworld.android:id/image'},
      ],
    })
    const result = await eyes.close(undefined)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['imageMatchSettings']['ignore']['0'],
      {left: 53, top: 0, width: 254, height: 22},
      undefined
    )
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['imageMatchSettings']['ignore']['1'],
      {left: 0, top: 21, width: 360, height: 234},
      undefined
    )
  })
})
