// appium android check window
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({
      device: 'Samsung Galaxy S8',
      app:
        'https://applitools.bintray.com/Examples/eyes-android-hello-world.apk',
    })
    eyes = testSetup.getEyes({
      displayName: 'appium android check window',
      baselineName: 'Appium_Android_CheckWindow',
      branchName: 'javascript_mobile',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('appium android check window (@native-selectors @native @mobile @headfull)', async () => {
    await spec.click(driver, {
      type: 'class name',
      selector: 'android.widget.Button',
    })
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'Appium_Android_CheckWindow',
      undefined
    )
    await eyes.check({
      ignoreRegions: [{type: 'class name', selector: 'android.widget.Button'}],
    })
    const result = await eyes.close(undefined)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['imageMatchSettings']['ignore']['0'],
      {left: 136, top: 237, width: 90, height: 48},
      undefined
    )
  })
})
