// should send dom and location when check region by selector fully with custom scroll root
const cwd = process.cwd()
const path = require('path')
const assert = require('assert')
const spec = require(path.resolve(cwd, 'src/spec-driver'))
const {testSetup, getTestInfo, getDom} = require('@applitools/sdk-shared')

describe('Coverage Tests', () => {
  let driver, destroyDriver, eyes
  beforeEach(async () => {
    ;[driver, destroyDriver] = await spec.build({browser: 'chrome'})
    eyes = testSetup.getEyes({
      displayName:
        'should send dom and location when check region by selector fully with custom scroll root',
      baselineName:
        'ShouldSendDomAndLocationWhenCheckRegionBySelectorFullyWithCustomScrollRoot',
    })
  })
  afterEach(async () => {
    await destroyDriver(driver)
    await eyes.abort()
  })
  it('should send dom and location when check region by selector fully with custom scroll root', async () => {
    await spec.visit(
      driver,
      'https://applitools.github.io/demo/TestPages/FramesTestPage/'
    )
    await eyes.open(
      driver,
      'Applitools Eyes SDK',
      'ShouldSendDomAndLocationWhenCheckRegionBySelectorFullyWithCustomScrollRoot',
      {width: 700, height: 460}
    )
    await spec.executeScript(driver, 'window.scrollTo(0, 350)', ...[])
    await spec.click(driver, '#centered')
    await eyes.check({
      region: '#modal-content',
      isFully: true,
      scrollRootElement: '#modal1',
    })
    const result = await eyes.close(false)
    const info = await getTestInfo(result)
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['image']['location'],
      {x: 104, y: 38},
      undefined
    )
    assert.deepStrictEqual(
      info['actualAppOutput']['0']['image']['hasDom'],
      true,
      undefined
    )
    const dom = await getDom(
      result,
      info['actualAppOutput']['0']['image']['domId']
    )
    assert.deepStrictEqual(
      dom['childNodes']['1']['childNodes']['33']['attributes'][
        'data-applitools-scroll'
      ],
      'true',
      undefined
    )
  })
})
